sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    try {
        var r = new sn_ws.RESTMessageV2('x_1798295_weathe_0.weatherAPI', 'Default GET');
        r.setQueryParameter('q', current.enter_location);
        var response = r.executeAsync();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();
        var responseObj = JSON.parse(responseBody);
        current.temperature_in_c = responseObj.current.temp_c;
        current.localtime = responseObj.location.localtime;
        current.windspeed = responseObj.current.wind_kph;
        current.present_condition = responseObj.current.condition.text;
        current.state = responseObj.location.region;
        current.country = responseObj.location.country;
        current.update();
    } catch (ex) {
        var message = ex.message;
    }

})(current, previous);
